<?include("config.php");$user = clear($_GET['user']);$user_qa = $db->query("SELECT `rname`,`username`,`nastroenie`,`avatar`,`born` FROM `users` WHERE `username`='$user'");$user = $user_qa->fetch_object();if ( empty($user->nastroenie) ) { $nastr = '<i>Не е въведено.</i>'; } else { $nastr = $user->nastroenie; }$template->assign_vars(array(						"img" => $user->avatar,						"name" => $user->username,						"rname" => $user->rname,						"nastr" => $nastr,						"born" => $user->born						)); $template -> pparse("check_user");?>